INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (4, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '02b1c3be-aac5-4867-9361-ca724b87764e', 1681469894, 'Artificial Intelligence (AI) refers to the ability of machines to perform tasks that typically require human intelligence, such as speech recognition, decision-making, problem-solving, and language translation. AI involves the development and application of algorithms and statistical models that allow machines to learn from data and improve their performance over time. AI is used in a wide variety of applications, including image and speech recognition, natural language processing, autonomous vehicles, predictive analytics, and robotics. While AI has the potential to revolutionize many industries, it also raises ethical concerns around issues such as job displacement, privacy, and bias.', 1, 1, 1, 1, 0);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (5, '02b1c3be-aac5-4867-9361-ca724b87764e', 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 1681557485, '[{\"role\": \"user\", \"content\": \"“哑巴英语”怎么翻译成英语？怎么地道的表示自己的英语能力停留在听和读的初级水平，在口语交流方面很欠缺？\"}]', 1, 1, 1, 1, 0);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (6, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '02b1c3be-aac5-4867-9361-ca724b87764e', 1681557491, '“哑巴英语”在英语中可以翻译为“broken English”或者“poor English”。要表达自己的英语能力停留在初级水平，在口语交流方面有欠缺，可以使用“my speaking skills in English are limited”或者“my English speaking ability is not very strong”。', 1, 1, 1, 1, 0);
