INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (87, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 'c3efccd6-f826-497b-8530-00f84fc50dc6', 1682214121, 'Rawls\' theory of justice argues that a just society must ensure that everyone has equal basic rights and opportunities, and that any social and economic inequalities must be arranged so that they are both to the greatest benefit of the least advantaged and attached to positions and offices open to all under conditions of fair equality of opportunity. This is known as the \"veil of ignorance\" principle, which means that individuals in a just society should make decisions without knowing their own social status or position, so that they can make fair and impartial decisions that benefit everyone, regardless of their social standing.', 1, 1, 1, 1, 0);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (88, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 'c3efccd6-f826-497b-8530-00f84fc50dc6', 1682214129, '\"罗尔斯的正义论说什么\" 可以翻译为 \"What does John Rawls\' theory of justice say?\"。\n\n这句话中的 \"what\" 是一个疑问词，用来引导一个疑问句。句子中的 \"does\" 是助动词，用来构成一般疑问句。\"John Rawls\' theory of justice\" 是一个名词短语，用来指代一个人的正义理论。整个句子的语法结构是一个简单的一般疑问句。', 1, 1, 1, 1, 0);
