INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (263, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '25b504c1-439d-4e7b-baf3-d87c6fdba3bc', 1682235735, '\"手机\"的英文翻译是 \"mobile phone\" 或 \"cell phone\"。其中，\"mobile\"是形容词，表示 \"可移动的\"；\"phone\"是名词，表示 \"电话\"。在英语中，通常使用 \"mobile phone\" 或 \"cell phone\" 来指代 \"手机\"。\n\n涉及的语法知识是复合词。在英语中，可以通过将两个或更多的单词组合在一起来形成复合词。例如，\"mobile phone\" 就是由 \"mobile\" 和 \"phone\" 组合而成的复合词。复合词在英语中非常常见，可以用来表示各种事物，如 \"bookshelf\"（书架）、\"headphones\"（耳机）、\"sunflower\"（向日葵）等。', 1, 1, 1, 1, 0);
