INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (446, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '02b1c3be-aac5-4867-9361-ca724b87764e', 1682678043, '单词`probationary`\n中文解释：试用的，实习的\n英文解释：relating to a period of time during which someone is being tested or watched to see if they are suitable for a particular job or activity\n词根词缀信息：词根是“probation-”，意为“试用、缓刑”，后缀是“-ary”，表示形容词。\n例句：\n- The new employee is on a probationary period of three months.（新员工处于为期三个月的试用期。）\n- The probationary period for new police officers is one year.（新警察的试用期为一年。）\n- During the probationary period, the employee\'s performance will be closely monitored.（在试用期间，员工的表现将会受到密切监视。）', 1, 1, 1, 1, 0);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (447, '189bbcb8-3cec-427c-a313-b9b134f03985', 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 1682689279, '昨天我和闺蜜一起去做手工，我们边做手工边聊天。我们共同话题就是学校、同学、老师，充分表达各自的学习压力，相互吐槽，真是一吐为快。', 1, 1, 1, 1, 0);
