INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (449, '189bbcb8-3cec-427c-a313-b9b134f03985', 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 1682689478, '昨天，我和闺蜜一起相约，去做我们喜欢的手工。边做边聊天，我们聊天的主题无非是各自的学校、老师和同学，还有没完没了的作业和考试。真是一吐为快呀。', 1, 1, 1, 1, 0);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (450, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '189bbcb8-3cec-427c-a313-b9b134f03985', 1682689489, 'Yesterday, my best friend and I made an appointment to do our favorite handicrafts together. While we were working, we chatted about our schools, teachers, classmates, and the never-ending homework and exams. It was really cathartic.\n\n这个句子中涉及了以下语法知识：\n1. 动词短语 \"made an appointment\" 表示约定，其中的动词 \"made\" 是一个过去式，表示动作发生在过去。\n2. \"to do\" 是不定式，表示目的，用于表达我们约定的目的是做手工。\n3. \"while we were working\" 是一个时间状语从句，表示我们聊天的时间是在做手工的同时。\n4. \"were chatting\" 是过去进行时，表示过去某一时刻正在进行的动作。\n5. \"about\" 是介词，表示我们聊天的主题是关于学校、老师、同学、作业和考试。\n6. \"It was really cathartic\" 中的 \"It\" 指代的是我们聊天的内容，\"cathartic\" 是形容词，表示它让我们感到畅快。', 1, 1, 1, 1, 0);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (451, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '189bbcb8-3cec-427c-a313-b9b134f03985', 1682689553, 'That sounds like a fun and productive activity! What kind of handcrafts did you and your friend make?', 1, 1, 1, 1, 0);
