INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (467, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '189bbcb8-3cec-427c-a313-b9b134f03985', 1682691269, '电影《夺冠》是一部以真实事件为背景的电影，讲述了中国女排在20世纪80年代的艰苦备战和最终夺得世界冠军的故事。电影通过女排队员们的努力、坚持和团结，展现了中国精神和中华民族的凝聚力。', 1, 1, 1, 1, 0);
