INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (489, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '189bbcb8-3cec-427c-a313-b9b134f03985', 1682697024, '尼摩船长是法国作家儒勒·凡尔纳的小说《海底两万里》中的主要角色，是一位神秘的海洋探险家和科学家。他是一个极其聪明、有才华和有追求的人，对科学和技术有着深厚的兴趣和热情，是一位极具冒险精神的人物。他的性格十分复杂，既有坚定的信仰和原则，又有强烈的自由主义和反抗精神，对人类社会的黑暗面和不公正现象深感不满，因此选择了远离陆地，独自在海底生活和探险。他对待船员和其他人的态度也是十分矛盾的，既有时候表现出温和、友好和慷慨的一面，也有时候表现出冷酷、高傲和独断的一面。总之，尼摩船长是一个充满神秘和矛盾性格的人物，他的形象深深地吸引着读者的注意力。', 1, 1, 1, 1, 0);
