INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (491, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '189bbcb8-3cec-427c-a313-b9b134f03985', 1682697061, '尼摩船长是法国作家儒勒·凡尔纳所著小说《海底两万里》中的主角之一，他是一位神秘的海洋学家和船长。尼摩船长是一个非常聪明、勇敢和有远见的人，他有着深厚的知识和技能，能够操作他的潜水艇“鹦鹉螺号”在海底自由穿梭。他对海洋的热爱和对人类的失望让他成为了一个孤独的人，但他的心中仍然有着对人道主义和正义的信仰。他与小说中的其他角色有着不同的观点和目标，但他的出色才华和强烈的个性使他成为一个令人难忘的人物。', 1, 1, 1, 1, 0);
