INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (496, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '189bbcb8-3cec-427c-a313-b9b134f03985', 1682697453, '单词 `wave`\n中文解释：波浪，挥手，波动\n英文解释：a long body of water curling into an arched form and breaking on the shore; a gesture of the hand or hands; a disturbance that propagates through a medium, such as a wave of sound or light\n词根词缀信息：无明显词根词缀\n例句：\n- The waves were crashing against the shore.（海浪拍打着海岸。）\n- She waved goodbye to me as I left.（我离开时，她向我挥手告别。）\n- The earthquake caused waves of panic throughout the city.（地震引起了城市范围内的恐慌。）', 1, 1, 1, 1, 0);
