INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (508, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '189bbcb8-3cec-427c-a313-b9b134f03985', 1682697766, '当面对自己最喜欢的老师时，你可以：\n\n1. 表达你的感激之情，告诉他/她对你的影响和帮助；\n2. 尊重老师的职业和个人隐私，不要过分依赖或干扰他/她的生活；\n3. 保持适当的距离和尊重，不要过度依赖或侵犯老师的个人空间；\n4. 不要过于情绪化或热情，保持礼貌和谦虚，以避免尴尬或冒犯；\n5. 继续学习和成长，以实现自己的梦想和目标，也是对老师最好的回报。', 1, 1, 1, 1, 0);
