INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (658, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '189bbcb8-3cec-427c-a313-b9b134f03985', 1683378208, '在太空中，宇航员的身体不再受到地球引力的约束，因此他们的肌肉和骨骼会逐渐失去负荷。这种失重状态会导致宇航员的肌肉和骨骼组织逐渐变弱，甚至会引起身体不适和晕眩等症状。为了避免这种情况，宇航员需要进行特殊的锻炼和饮食计划，以保持身体健康和适应太空环境。', 1, 1, 1, 1, 0);
