INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (661, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '189bbcb8-3cec-427c-a313-b9b134f03985', 1683378656, '宇航员在太空中会出现过载情形是因为在宇宙中，没有地心引力的作用，身体的重力负荷减小，血液会向上流动，导致头部充血，脑部供氧不足，出现晕厥、头痛等症状。此外，宇航员在太空中进行任务时，需要承受高加速度的运动，如火箭发射、航天器变轨等，也会导致身体过载。为了避免这种情况，宇航员需要进行特殊的训练和身体适应性调整。', 1, 1, 1, 1, 31);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (662, '189bbcb8-3cec-427c-a313-b9b134f03985', 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 1683378899, '那么宇航员在太空中出现负荷又是怎么回事', 1, 1, 1, 1, 31);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (663, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '189bbcb8-3cec-427c-a313-b9b134f03985', 1683378905, '宇航员在太空中出现负荷是指身体承受的重力负荷减小，这是由于在太空中没有地心引力的作用。身体在地球上习惯了承受一定的重力负荷，但在太空中，身体的重力负荷减小，导致肌肉和骨骼失去了原有的负荷，容易出现肌肉萎缩和骨质疏松等问题。为了避免这种情况，宇航员需要进行特殊的训练和身体适应性调整，以保持身体的健康和适应太空环境。', 1, 1, 1, 1, 31);
