INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (665, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '189bbcb8-3cec-427c-a313-b9b134f03985', 1683379005, '空间运动病是指在太空中进行高加速度运动时，宇航员出现的晕眩、恶心、呕吐等症状。这是由于在太空中，没有地心引力的作用，身体的平衡感和空间定向感受到了挑战，导致内耳和视觉系统的信息不一致，从而引起空间运动病。为了避免这种情况，宇航员需要进行特殊的训练和身体适应性调整，以提高身体的耐受性和适应太空环境。同时，太空任务中也会采取一些措施，如减少运动的幅度和频率，使用药物等，来缓解空间运动病的症状。', 1, 1, 1, 1, 31);
