INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (782, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '0ed29253-6ed7-4838-b84e-869ba9632805', 1683861523, 'GFW（Great Firewall of China）防火墙是中国政府为了控制互联网内容而建立的一种网络过滤系统。它可以监控和过滤进出中国的网络流量，阻止访问被认为是敏感或不合适的网站、应用程序和服务。GFW防火墙使用多种技术，包括IP封锁、DNS污染、HTTP过滤、SSL中间人攻击等，以实现其目的。', 1, 1, 1, 1, 40);
