INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (816, 'e807a6d8-e499-4c14-b492-fb16c7289030', 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 1683961117, '帮我写一个画室的简介，1500字左右。这家画室的教学理念非常先进，对于每个学生的水平和进度情况有充分的评估和教学方法和教学计划上的适应性，从美术技能教育和应试教育角度实现了很好的平衡。画室在成立的8年间，陆续培养和输送了很多优秀学员到中国高等艺术学府。轻帮我充分发散和美化这些简介。', 1, 1, 1, 1, 0);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (817, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 'e807a6d8-e499-4c14-b492-fb16c7289030', 1683961139, '这是一家非常先进的画室，其教学理念独具匠心。在这里，每个学生都会得到充分的评估，根据自己的水平和进度情况，制定出适合自己的教学方法和教学计划。画室在美术技能教育和应试教育方面实现了很好的平衡，让学生在学习美术技能的同时，也能够为应试教育做好准备。\n\n成立的8年间，这家画室培养和输送了很多优秀学员到中国高等艺术学府，这充分说明了画室的教学质量和教学成果。在这里，学生们不仅能够得到专业的美术技能培训，还能够得到全方位的指导和帮助，让他们在未来的学习和工作中更加得心应手。\n\n画室的师资力量非常强大，拥有一批经验丰富、教学水平高超的老师。他们不仅具备深厚的美术功底，还拥有丰富的教学经验和教学方法，能够帮助学生快速提升自己的美术水平。\n\n画室的教学环境也非常优美，宽敞明亮的教室、舒适的座椅、先进的教学设备和工具，都为学生提供了一个良好的学习氛围。同时，画室还注重培养学生的综合素质，为学生提供各种文化课程和活动，让学生在学习美术的同时，也能够拓宽自己的知识面和视野。\n\n总之，这是一家非常优秀的画室，它的教学理念先进、教学质量高、师资力量强大、教学环境优美，无疑是你学习美术的最佳选择。', 1, 1, 1, 1, 0);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (818, 'e807a6d8-e499-4c14-b492-fb16c7289030', 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 1683962422, '我开了一家线下画室，是正规的教美术专业技法的机构，想要在互联网上社交平台开设官方账号进行宣传。请帮我写第一个帖子，突出教学理念的先进性和优秀的应试效果。尽量美化和发散一些，2000字左右。', 1, 1, 1, 1, 42);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (819, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 'e807a6d8-e499-4c14-b492-fb16c7289030', 1683962449, '亲爱的朋友们，大家好！我是一家线下画室的创始人，非常高兴能够在这里与大家分享我们的教学理念和优秀的应试效果。\n\n我们的画室致力于教授美术专业技法，让每一个学生都能够在这里找到自己的艺术天赋和潜力。我们的教学理念非常先进，我们不仅注重学生的技术水平，更注重学生的创造力和想象力的培养。我们相信，艺术不仅仅是一种技能，更是一种生活态度和思维方式。因此，我们的教学方法不仅仅是传授技巧，更是引导学生去发现和探索自己的艺术世界。\n\n我们的教学团队由一批经验丰富、富有创造力和热情的艺术家组成。他们不仅拥有深厚的艺术功底，更具备教学经验和教学方法的创新能力。他们会根据每个学生的特点和需求，量身定制教学计划，让每个学生都能够得到最好的教学效果。\n\n我们的教学成果也非常优秀。我们的学生不仅在各种艺术比赛中屡获佳绩，更在各大艺术院校中获得了优秀的录取成绩。这些成绩的取得，不仅仅是学生自身的努力，更是我们教学团队的辛勤付出和教学方法的成功应用。\n\n我们的画室不仅仅是一个教学机构，更是一个艺术家的聚集地。在这里，学生们不仅能够得到专业的教学指导，更能够结交到各种不同背景和风格的艺术家，拓展自己的艺术视野和思维方式。我们相信，艺术是一种交流和分享的方式，我们希望能够通过我们的画室，让更多的人能够感受到艺术的魅力和力量。\n\n最后，我想说的是，我们的画室欢迎每一个对艺术有兴趣的人来参加我们的课程和活动。我们相信，每个人都有自己的艺术潜力和天赋，只要你愿意去发现和探索，就一定能够成为一个优秀的艺术家。让我们一起走进艺术的世界，感受艺术的魅力和力量！', 1, 1, 1, 1, 42);
