INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (821, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 'e807a6d8-e499-4c14-b492-fb16c7289030', 1683962549, '雪花飘飘，漫天飞舞，\n银装素裹，美不胜收。\n寒冬之中，它是一抹亮色，\n让人们感受到生命的活力。\n\n雪花纷纷，落在树枝上，\n形成了一幅幅美丽的画卷。\n它们轻盈飘逸，如同精灵般，\n让人们心中充满了美好的憧憬。\n\n雪花飞舞，似乎在诉说着什么，\n是对生命的赞美，还是对未来的期许？\n无论如何，它们都是美丽的，\n让人们感受到了生命的无限可能。\n\n雪花纷飞，让人们感到温暖，\n它们是一种美好的存在，\n让人们在寒冷的冬季中感受到了温馨和爱。\n让我们一起欣赏雪的美丽，\n感受生命的无限魅力！', 1, 1, 1, 1, 42);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (822, 'e807a6d8-e499-4c14-b492-fb16c7289030', 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 1683962605, '左手关于雨的诗，七言绝句，不得抄袭古诗', 1, 1, 1, 1, 42);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (823, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 'e807a6d8-e499-4c14-b492-fb16c7289030', 1683962619, '雨丝细细，轻拂窗棂，\n滋润大地，滋养万物。\n雨声悠悠，如诗如画，\n让人心旷神怡，感受自然的美妙。\n\n雨滴落地，泛起涟漪，\n如同生命的起伏，充满了变幻。\n雨水滋润，滋养着生命，\n让我们感受到生命的无限可能。\n\n雨中漫步，感受自然的韵律，\n让我们沉浸在大自然的怀抱中。\n雨中思考，感悟生命的真谛，\n让我们更加珍惜生命，感恩自然的恩赐。\n\n雨丝细细，如同生命的细节，\n让我们更加注重生命的每一个瞬间。\n雨声悠悠，如同心灵的呼唤，\n让我们更加关注内心的声音，感受生命的美好。\n\n左手写下这首关于雨的诗，\n是为了表达对自然的敬畏和感恩。\n让我们一起珍惜每一个雨滴，\n感受生命的美妙和无限魅力！', 1, 1, 1, 1, 42);
