INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (827, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '25b504c1-439d-4e7b-baf3-d87c6fdba3bc', 1683981105, '\"去杭州转转有什么地方推荐的？\" 可以翻译为 \"Are there any recommended places to visit in Hangzhou?\"。\n\n这句话中涉及了疑问句的语法知识，疑问句是用来询问信息、确认信息或者提出疑问的句子。在英语中，疑问句通常以助动词或者疑问词开头。在这个例子中，疑问词是 \"what\"，用来询问有哪些地方值得去参观。', 1, 1, 1, 1, 0);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (828, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '25b504c1-439d-4e7b-baf3-d87c6fdba3bc', 1683981113, '\"去杭州转转有什么地方推荐的？\" 可以翻译为 \"Are there any recommended places to visit in Hangzhou?\"。\n\n该句中的主语为 \"there\"，表示 \"有\" 的意思，而 \"any recommended places\" 则是主语的补语，表示 \"推荐的地方\"。动词为 \"are\"，表示 \"是\" 的意思。\"to visit\" 是不定式，表示 \"参观\" 的意思。在疑问句中，动词通常位于主语之后。', 1, 1, 1, 1, 0);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (829, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '25b504c1-439d-4e7b-baf3-d87c6fdba3bc', 1683981119, 'There are many places worth visiting in Hangzhou. West Lake, Lingyin Temple, and Hefang Street are some of the most popular tourist attractions. Other places of interest include Xixi Wetland Park, Qinghefang Ancient Street, and Leifeng Pagoda.', 1, 1, 1, 1, 0);
