INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (833, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '25b504c1-439d-4e7b-baf3-d87c6fdba3bc', 1683981852, 'Suzhou is a city located in the eastern part of China, in the Jiangsu province. It is known for its beautiful gardens, canals, and silk production. The city has a long history, dating back over 2,500 years, and is often referred to as the \"Venice of the East\" due to its numerous canals and waterways. Suzhou is also a major tourist destination, attracting millions of visitors each year.', 1, 1, 1, 1, 0);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (834, '25b504c1-439d-4e7b-baf3-d87c6fdba3bc', 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 1683982597, '去苏州玩三天怎么玩', 1, 1, 1, 1, 0);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (835, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '25b504c1-439d-4e7b-baf3-d87c6fdba3bc', 1683982608, 'Sure, here are some suggestions for a three-day trip to Suzhou:\n\nDay 1:\n- Visit the Humble Administrator\'s Garden, one of the most famous classical gardens in China\n- Explore Pingjiang Road, a historic street with traditional architecture, shops, and restaurants\n- Enjoy a boat ride on the Grand Canal\n\nDay 2:\n- Visit the Lingering Garden, another beautiful classical garden\n- Explore the Suzhou Museum, which showcases the city\'s history and culture\n- Visit the Suzhou Silk Museum to learn about the city\'s silk industry\n\nDay 3:\n- Visit Tiger Hill, a scenic area with a leaning pagoda and beautiful gardens\n- Explore Guanqian Street, a popular shopping and dining area\n- Visit the Master of Nets Garden, a small but exquisite classical garden\n\nHope this helps!', 1, 1, 1, 1, 0);
