INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (883, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '189bbcb8-3cec-427c-a313-b9b134f03985', 1684728382, '是指由于某种原因或事件导致的直接的财务损失，如物品损坏、生产停滞、人员伤亡等所造成的经济损失。这些损失可以通过货币或其他可计量的方式来衡量，通常会对企业或个人的财务状况产生直接的影响。直接经济损失是灾害或事故发生后最容易被看到和计算的损失，但它并不包括间接经济损失，如失去的商机、客户流失、信誉受损等。', 1, 1, 1, 1, 48);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (884, '189bbcb8-3cec-427c-a313-b9b134f03985', 'b811abd7-c0bb-4301-9664-574d0d8b11f8', 1684732743, '矿山开采过程中 政府以矿山被划入环境保护区而被停产 政府要赔偿吗', 1, 1, 1, 1, 48);
INSERT INTO `t_chat_record` (`pk_chat_record`, `msgFrom`, `msgTo`, `msgCreateTime`, `msgContent`, `msgStatus`, `msgType`, `msgSource`, `msgDest`, `conversation_id`) VALUES (885, 'b811abd7-c0bb-4301-9664-574d0d8b11f8', '189bbcb8-3cec-427c-a313-b9b134f03985', 1684732751, '这需要具体情况具体分析，一般来说，如果政府在矿山开采前已经明确告知矿山企业该区域将被划入环境保护区，企业在知情的情况下仍然选择在该区域进行开采，那么政府不需要承担赔偿责任。但如果政府在矿山开采过程中突然将该区域划入环境保护区，导致矿山企业无法继续开采，那么政府可能需要承担一定的赔偿责任。具体情况需要根据相关法律法规和合同约定来确定。', 1, 1, 1, 1, 48);
